mapper.select:
SELECT * FROM(SELECT a.*, rownum rnum FROM(SELECT b.num, b.name, b.category, b.start_date, b.finish_date, b.goal_amount, b.m_num, b.approval, b.photo, b.ship FROM project b WHERE b.m_num=#{m_num}
AND b.name LIKE '%' || #{keyword} || '%'
ORDER BY b.num DESC)a)
WHERE rnum >= #{start} AND rnum <= #{end}
